function initMap(){map=L.map("map").setView([0,0],2);L.Icon.Default.imagePath="lib/leaflet/images/";const n=L.tileLayer("https://tile.openstreetmap.org/{z}/{x}/{y}.png",{maxZoom:19,attribution:'&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap<\/a>'}).addTo(map)}function addMarker(n,t,i){var r=L.marker([n,t]).bindTooltip(i,{permanent:!0,direction:"right"}).addTo(map)}function clearMarkersAndPaths(){}function drawPath(n){var t=Array(),i;for(let i=0;i<n.length;i++)if(n[i].details&&n[i].details.latitude&&n[i].details.longitude){const r={lat:n[i].details.latitude,lon:n[i].details.longitude};t.push(r)}i=L.polyline(t,{color:"red"}).addTo(map);map.fitBounds(i.getBounds())}var map;$(function(){initMap()});