function initMap(){map=L.map("map").setView([0,0],2);L.Icon.Default.imagePath="lib/leaflet/images/";const n=L.tileLayer("https://tile.openstreetmap.org/{z}/{x}/{y}.png",{maxZoom:19,attribution:'&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap<\/a>'}).addTo(map)}function addMarker(n,t,i){var r=L.marker([n,t]).bindTooltip(i,{permanent:!0,direction:"right"}).addTo(map);markers.push[r]}function clearMarkersAndPaths(){map.eachLayer(n=>{(n._latlng!=undefined||n._path!=undefined)&&n.remove()});markers=[];polyline=undefined}function drawPath(n){var t=[],i;if(n.find(n=>n.details==undefined||n.details.isp==undefined)==undefined){for(let r=0;r<n.length;r++)n[r].details&&n[r].details.latitude&&n[r].details.longitude&&(i=new L.latLng(n[r].details.latitude,n[r].details.longitude),t.push(i));polyline=L.polyline(t,{color:"red"}).addTo(map);map.fitBounds(polyline.getBounds())}}var map,polyline,markers=[];$(function(){initMap()});